import React from 'react';
import { useMutation, gql } from "@apollo/client";

const getEditTrackQuery = ({ id, name, start, end }) => {
   return `mutation Create {
      addTrack(
         id: "${id}"
         name: "${name}"
         start: "${start}"
         end: "${end}"
      ) {
         id
         name
         start
         end
      }
   }`
}

const getDeleteTrackQuery = (id) => {
   return `mutation Create {
      deleteTrack(
         id: "${id}"
      ) {
         id
         name
         start
         end
      }
   }`
}


const Card = ({info, refreshTracker}) => {
   const EDIT_TRACKER = gql`${getEditTrackQuery(info)}`;
   const DELETE_TRACKER = gql`${getDeleteTrackQuery(info.id)}`;

   const [editTrackerInfo] = useMutation(EDIT_TRACKER);
   const [deleteTrackerInfo] = useMutation(DELETE_TRACKER);

   const editTrack = () => {
      editTrackerInfo();
      refreshTracker();
   }

   const deleteTrack = () => {
      deleteTrackerInfo();
      refreshTracker();
   }

   return (
      <div className="card" style={{'width': '100%', 'marginTop': '10px'}}>
         <div className="card-body">
            <div className="d-flex justify-content-between">
               <h4 className="card-title">{info.name}</h4>
               <h5>Timer</h5>
            </div>
            <h6 className="card-subtitle mb-2 text-muted">Start: {info.start}</h6>
            <h6 className="card-subtitle mb-2 text-muted">End: {info.end}</h6>
            <div className="d-flex justify-content-between mt-3">
               <button className="btn btn-success">Start Time</button>
               <button className="btn btn-info">Stop Time</button>
               <button className="btn btn-danger" onClick={deleteTrack}>Delete Card</button>
            </div>
         </div>
      </div>
   )
};

export default Card;
