import React, { useState } from 'react';
import { useMutation, gql } from "@apollo/client";

const EDIT_TRACKER = gql`
   mutation Update(
      $id: ID!
      $name: String!
      $start: String!
      $end: String!
   ) {
      editTrack(
         id: $id
         name: $name
         start: $start
         end: $end
      ) {
         id
         name
         start
         end
      }
   }`;

const DELETE_TRACKER = gql`
   mutation Delete($id: ID!) {
      deleteTrack(id: $id) {
      ok
      }
   }`;


const Card = ({cardData, refreshTracker}) => {
   const [info, setInfo] = useState(cardData);

   const [editTrackerInfo] = useMutation(EDIT_TRACKER);
   const [deleteTrackerInfo] = useMutation(DELETE_TRACKER);

   const editTrack = () => {
      editTrackerInfo({variables: {
         id: info.id,
         name: info.name,
         start: info.start,
         end: info.end
      }});
      refreshTracker();
   }

   const deleteTrack = () => {
      deleteTrackerInfo({variables: {id: info.id}});
      refreshTracker();
   }

   return (
      <div className="card" style={{'width': '100%', 'marginTop': '10px'}}>
         <div className="card-body">
            <div className="d-flex justify-content-between">
            <input type="name" placeholder="Name..."
               value={info.name}
               onChange={e => (setInfo(
                  {
                     ...info,
                     name: e.target.value
                  }
               ))}/>
               <h5>Timer</h5>
            </div>
            <h6 className="card-subtitle mb-2 text-muted">Start: {info.start}</h6>
            <h6 className="card-subtitle mb-2 text-muted">End: {info.end}</h6>
            <div className="d-flex justify-content-between mt-3">
               <button className="btn btn-success" onClick={editTrack}>Start Time</button>
               <button className="btn btn-info">Stop Time</button>
               <button 
                  className="btn btn-danger" 
                  onClick={deleteTrack}
               >
                  Delete Card
               </button>
            </div>
         </div>
      </div>
   )
};

export default Card;
