import React, { useEffect, useState } from 'react';
import { useMutation, gql } from "@apollo/client";

const EDIT_TRACKER = gql`
   mutation Update(
      $id: ID!
      $name: String!
      $start: String!
      $end: String!
   ) {
      editTrack(
         id: $id
         name: $name
         start: $start
         end: $end
      ) {
         id
         name
         start
         end
      }
   }`;

const DELETE_TRACKER = gql`
   mutation Delete($id: ID!) {
      deleteTrack(id: $id) {
      ok
      }
   }`;


const Card = ({cardData, refreshTracker}) => {
   const [info, setInfo] = useState(cardData);

   useEffect(() => {
      setInfo(cardData);
   },[cardData]);

   const [editTrackerInfo] = useMutation(EDIT_TRACKER);
   const [deleteTrackerInfo] = useMutation(DELETE_TRACKER);

   const editTrack = () => {
      editTrackerInfo({variables: {
         id: info.id,
         name: info.name,
         start: info.start,
         end: info.end
      }});
      refreshTracker();
   };

   const deleteTrack = () => {
      deleteTrackerInfo({variables: {id: info.id}});
      refreshTracker();
   };

   const editNameInput = e => {
      if(e.target.value){
         setInfo({
            ...info,
            name: e.target.value
         })
      }
   };

   const editStartTime = () => {
      const timeStr = new Date().toLocaleString();

      editTrackerInfo({variables: {
         id: info.id,
         name: info.name,
         start: timeStr,
         end: info.end
      }});
      refreshTracker();
   }

   const editEndTime = () => {
      const timeStr = new Date().toLocaleString();

      editTrackerInfo({variables: {
         id: info.id,
         name: info.name,
         start: info.start,
         end: timeStr,
      }});
   }

   return (
      <div className="card" style={{'width': '100%', 'marginTop': '10px'}}>
         <div className="card-body">
            <div className="d-flex justify-content-between">
               <div class="input-group mb-3">
                  <input 
                     type="name" 
                     placeholder="Name..."
                     className="form-control col-3 border-0"
                     value={info.name}
                     maxLength="20"
                     onChange={editNameInput}/>

                  <div class="input-group-append">
                     <button 
                        class="btn btn-outline-secondary" 
                        type="button" 
                        onClick={editTrack}
                        >Save Changes
                     </button>
                  </div>
               </div>
            </div>

            <h6 className="card-subtitle mb-2 text-muted">Start: {info.start}</h6>

            <h6 className="card-subtitle mb-2 text-muted">End: {info.end}</h6>

            <div className="d-flex justify-content-between mt-3">
               <button 
                  className="btn btn-success" 
                  onClick={editStartTime} 
                  disabled={info.start}>
                     Start Time
               </button>

               <button 
                  className="btn btn-info" 
                  onClick={editEndTime} 
                  disabled={!info.start || info.end}>
                     Stop Time
               </button>

               <button 
                  className="btn btn-danger" 
                  onClick={deleteTrack}>
                  Delete Track
               </button>
            </div>
         </div>
      </div>
   )
};

export default Card;
